mapper.select:
select * 
from (select rownum rnum, NOTICE_ID, EMPLOYEE_ID, DEPARTMENT_ID, 
            TEAM_ID, NOTICE_TITLE, NOTICE_CONTENT, EDIT_DATE, 
            ATTACHEMENT_FILENAME, READ_COUNT, RENAME_FILENAME 
      from (select * from notice 
           where NOTICE_TITLE like '%' || #{ keyword } || '%' 
           order by EDIT_DATE desc)
     ) 
where rnum between #{ startRow } and #{ endRow }

select * 
from (select rownum rnum, NOTICE_ID, EMPLOYEE_ID, DEPARTMENT_ID, 
            TEAM_ID, NOTICE_TITLE, NOTICE_CONTENT, EDIT_DATE, 
            ATTACHEMENT_FILENAME, READ_COUNT, RENAME_FILENAME 
      from (select * from notice 
           where NOTICE_CONTENT like '%' || #{ keyword } || '%' 
           order by EDIT_DATE desc)
     ) 
where rnum between #{ startRow } and #{ endRow }

select * 
from (select rownum rnum, NOTICE_ID, EMPLOYEE_ID, DEPARTMENT_ID, 
            TEAM_ID, NOTICE_TITLE, NOTICE_CONTENT, EDIT_DATE, 
            ATTACHEMENT_FILENAME, READ_COUNT, RENAME_FILENAME 
      from (select * from notice 
           where EDIT_DATE between #{ begin } and #{ end } 
           order by EDIT_DATE desc)
     ) 
where rnum between #{ startRow } and #{ endRow }

mapper.update:
update notice 
set READ_COUNT = READ_COUNT + 1 
where NOTICE_ID = #{ noticeId }

update notice set EMPLOYEE_ID = #{employeeId}, NOTICE_TITLE = #{noticeTitle}, NOTICE_CONTENT = #{noticeContent}, EDIT_DATE = sysdate
, ATTACHEMENT_FILENAME = null, RENAME_FILENAME = null
, ATTACHEMENT_FILENAME = #{attachementFilename}, RENAME_FILENAME = #{renameFilename}
where NOTICE_ID = #{noticeId}
